use strict;
use warnings;

my @files = @ARGV;

print <<"END_SETTING";
# This file automatically generated by $0.
no Main;

END_SETTING

foreach my $file (@files) {
    print "# From $file\n\n";
    open(my $fh, "< $file") or die $!;
    print join('', <$fh>);
    close $fh;
}
